mapper.select:
SELECT a.product_id productId, a.name name, a.price price, a."desc" desc, b.addr addr, a.details details, a.cate_cd cateCd, a.cre_dt creDt, a.updt_dt updDt, a.user_lg userId, 'product-photos/' || a.image image FROM public.products a left join public."usersShop" b on a.user_lg = b.email WHERE a.cate_cd = #{cateCd}
AND a.is_publish = true
ORDER BY a.updt_dt DESC OFFSET #{page} ROWS FETCH FIRST 10 ROW ONLY

SELECT a.product_id productId, a.name name, a.price price, a."desc" desc, b.addr addr, a.details details, a.cate_cd cateCd, a.cre_dt creDt, a.updt_dt updDt, a.user_lg userId, 'product-photos/' || a.image image, is_publish publish FROM public.products a left join public."usersShop" b on a.user_lg = b.email WHERE a.user_lg = #{userId}
AND a.is_publish = true
ORDER BY a.updt_dt DESC OFFSET #{page} ROWS FETCH FIRST 10 ROW ONLY
